class Solution {
    public int[] solution(int[] answers) {
        int[] answer = {};
        int[] one = {1,2,3,4,5};
        int[] two = {2,1,2,3,2,4,2,5};
        int[] three = {3,3,1,1,2,2,4,4,5,5};
        int[] sum = {0, 0, 0};
        '<'
        int max = 0;
        
        for(int i = 0; i \< answers.length ; i++){
            
            if(one[i%5] == answers[i]){
                sum[0] += 1;
            }
            if(two[i%8] == answers[i]){
                sum[1] += 1;
            }
            if(three[i%10] == answers[i]){
                sum[2] += 1;
            }       
      }
               
        for(int i = 0; i < sum.length ; i++){
            if(max < sum[i]){
                max = sum[i];
            }
        }
        
        if(max == sum[0] && max == sum[1] && max == sum[2]){
            answer = new int[3];
            answer[0] = 1;
            answer[1] = 2;
            answer[2] = 3;
        }
        else if(max == sum[0] && max == sum[1]){
            answer = new int[2];
            answer[0] = 1;
            answer[1] = 2;
        }
        else if(max == sum[0] && max == sum[2]){
            answer = new int[2];
            answer[0] = 1;
            answer[1] = 3;
        }
        else if(max == sum[1] && max == sum[2]){
            answer = new int[2];
            answer[0] = 2;
            answer[1] = 3;
        }
        else if(max == sum[0]){
            answer = new int[1];
            answer[0] = 1;
        }
        else if(max == sum[1]){
            answer = new int[1];
            answer[0] = 2;
        }
        else if(max == sum[2]){
            answer = new int[1];
            answer[0] = 3;
        }
        
      
        return answer;
    }
}

```

